import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import styles from './PowerPlanstData.css';

class PowerPlantsData extends Component {

    constructor(props) {
        super(props);
        this.state = {

            items: [],
            isLoaded: true,

        };
    }

    componentDidMount(){
        this.interval = setInterval(() => this.setState({ time: Date.now() + 10 }), 1000);
        this.getFetch();
    }

    componentDidUpdate(){
        clearInterval(this.interval);
    }

    getFetch(){

        fetch('http://localhost:8081/power-plants')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json,
                })
            });
    }

    render () {

        let { isLoaded, items } = this.state;

        if(!isLoaded){
            return <div>Loading...</div>;
        }
        else
        {   
            return (
                <div>
                    <Table striped bordered hover variant="dark">
                        <thead>
                            <th className = {styles.TableTop}>Name</th>
                            <th>Company Name</th>
                            <th>Postal Code</th>
                            <th>City</th>
                        </thead>
                        <tbody>
                            {items.map((item) => {
                                return (
                                    <tr>
                                        <td>{item.name}</td>
                                        <td>{item.company_name}</td>
                                        <td>{item.plz}</td>
                                        <td>{item.city}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </Table>

                </div>
            );
        }

    }
}

export default PowerPlantsData;
