import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import styles from './PowerPlantsData.module.css';


class PowerPlantsData extends Component {


    constructor(props) {
        super(props);
        this.state = {

            items: [],
            isLoaded: true,
            searchString: null
        };
    }
    // Loop through API
    componentDidMount(){
        this.interval = setInterval(() => this.setState({ time: Date.now() + 10 }), 1000);
        this.getFetch();
    }

    componentDidUpdate(){
        clearInterval(this.interval);
    }

    getFetch = () => {

        fetch('http://localhost:8081/power-plants')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json
                })

            });
    }

    renderLoading = () => {
        return <div>Loading...</div>;
    }
    renderData(items) {
        return (

            <div>

                <div className = {styles.PowerPlanet}>
                    <Table>
                        <tbody>
                            <tr>
                                <th className = {styles.TableTop}>Federal Network Agency Number</th>
                                <th className = {styles.TableTop}>Name</th>
                                <th className = {styles.TableTop}>Company Name</th>
                                <th className = {styles.TableTop}>Postal Code</th>
                                <th className = {styles.TableTop}>City</th>
                                <th className = {styles.TableTop}>Address</th>
                                <th className = {styles.TableTop}>State</th>
                                <th className = {styles.TableTop}>Start Date</th>
                                <th className = {styles.TableTop}>Status</th>
                                <th className = {styles.TableTop}>Type</th>
                                <th className = {styles.TableTop}>Net Nominal Power MegaWatt</th>
                            </tr>
                        </tbody>
                        <tbody>
                            {items.map((item) => {
                                return (
                                    <tr>
                                        <td className = {styles.TableTop}>{item.bundesnetzagentur_number}</td>
                                        <td className = {styles.TableTop}>{item.name}</td>
                                        <td className = {styles.TableTop}>{item.company_name}</td>
                                        <td className = {styles.TableTop}>{item.plz}</td>
                                        <td className = {styles.TableTop}>{item.city}</td>
                                        <td className = {styles.TableTop}>{item.address}</td>
                                        <td className = {styles.TableTop}>{item.state}</td>
                                        <td className = {styles.TableTop}>{item.start_date}</td>
                                        <td className = {styles.TableTop}>{item.status}</td>
                                        <td className = {styles.TableTop}>{item.type}</td>
                                        <td className = {styles.TableTop}>{item.net_nominal_power_mw}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </Table>
                </div>  
            </div>


        )}

    render () {

        let { isLoaded, items, searchString } = this.state;

        // if(!isLoaded){
        //     return (this.renderLoading);
        // }
        // else
        // { 
            return(
                items.filter((data)=>{
                    if(searchString == null)
                        return (this.state.renderData(data));
                    else if(data.type.toLowerCase().includes(searchString.toLowerCase())){
                        return (console.log(data),
                                console.log("filter data"));
                    }
                  })
            )


        // }

    }
}

export default PowerPlantsData;
