import React, {Component} from 'react';
import './App.css';
import PowerPlantsData from './components/PowerPlants/PowerPlantsData.js';
import SearchFilter from './components/Filters/SearchFilters.js';

const options = [
  { value: 'WIND_ONSHORE', label: 'Wind' },
  { value: 'BIOMASS', label: 'Biomass' },
  { value: 'HYDRO', label: 'Hydro' },
  { value: null , label: 'All' }
];

class App extends Component {

  state = {
    selectedOption: null,
  };

  handleChange = selectedOption => {
    this.setState({ selectedOption });
  };

  searchButton(){
    <div className={styles.selectButton} >
              <p>Select The Plant Type: </p>
                <Select value = {this.selectedOption}
                        onChange = {this.handleChange} 
                        options = {options} />              

          </div>
  }

  render() {

    return (
      <div className="App">

        <h1>Power Plants Data!</h1>
        <PowerPlantsData searchString = 'HYDRO' />
      </div>
    );
  }
}

export default App;
