import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import styles from './PowerPlantsData.module.css';


class PowerPlantsData extends Component {

    constructor(props) {
        super(props);
        this.state = {

            items: [],
            isLoaded: true,
            searchString: '',
            roleFilter: null
        };
    }

    componentDidMount(){
        this.interval = setInterval(() => this.setState({ time: Date.now() + 10 }), 1000);
        this.getFetch();
    }
    handleChange = e => {
        this.setState({ searchString: e.target.value.trim().toLowerCase() });
    }
    componentDidUpdate(){
        clearInterval(this.interval);
    }

    getFetch(){

        fetch('http://localhost:8081/power-plants')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json,
                })
            });
    }

    render () {

        let { isLoaded, items } = this.state;

        if(!isLoaded){
            return <div>Loading...</div>;
        }
        else
        {   
            return (
                <div>
                    <div variant="light" striped bordered>
                        <Table>
                            <thead>
                                <th className = {styles.TableTop}>Federal Network Agency Number</th>
                                <th className = {styles.TableTop}>Name</th>
                                <th className = {styles.TableTop}>Company Name</th>
                                <th className = {styles.TableTop}>Postal Code</th>
                                <th className = {styles.TableTop}>City</th>
                                <th className = {styles.TableTop}>Address</th>
                                <th className = {styles.TableTop}>State</th>
                                <th className = {styles.TableTop}>Start Date</th>
                                <th className = {styles.TableTop}>Status</th>
                                <th className = {styles.TableTop}>Type</th>
                                <th className = {styles.TableTop}>Net Nominal Power MegaWatt</th>

                            </thead>
                            <tbody>
                                {items.map((item) => {
                                    return (
                                        <tr>
                                            <td>{item.bundesnetzagentur_number}</td>
                                            <td>{item.name}</td>
                                            <td>{item.company_name}</td>
                                            <td>{item.plz}</td>
                                            <td>{item.city}</td>
                                            <td>{item.address}</td>
                                            <td>{item.state}</td>
                                            <td>{item.start_date}</td>
                                            <td>{item.status}</td>
                                            <td>{item.type}</td>
                                            <td>{item.net_nominal_power_mw}</td>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </Table>
                    </div>
                        <div>
                            <input type="text" id="searchbar" value={searchString} onChange={this.handleChange}
                                placeholder="Search by Role" name="device">
                            </input>

                            <select className="category-select" name="categories" onChange={this.changedRole}>
                                <option value={''}></option>
                                {text.map(info => (
                                    <option value={info.role}>{info.role}</option>
                                ))}
                            </select>
                            {text.filter(info => roleFilter ? info.role.includes(roleFilter) : true).map(info => (
                                <div className="display">
                                    <span className="role">Role: {info.role}</span><span>, Salary: {info.salary}</span>
                                </div>
                            ))}
                        </div>                
                </div>
            );
        }

    }
}

export default PowerPlantsData;
